 package dailycode;public class QuadraticEquation {    public static void main(String[] args) {        double a = 1, b = -3, c = 2;        double d = b * b - 4 * a * c;        if (d > 0) {            double r1 = (-b + Math.sqrt(d)) / (2 * a);            double r2 = (-b - Math.sqrt(d)) / (2 * a);            System.out.println("Roots: " + r1 + ", " + r2);        } else if (d == 0) {            double r = -b / (2 * a);            System.out.println("One Root: " + r);        } else {            System.out.println("Imaginary Roots");        }    }}